import React, { useState } from "react";
import styles from "./index.module.scss";
import image from "./palm-print.jpg";
const AddCard = ({ showForm = false, toggleAddCard }) => {
  const [name, setName] = useState("");
  const [skill, setSkill] = useState("");
  const [email, setEmail] = useState("");
  console.log("IMAGE", image);
  const handleFormClick = e => {
    e.stopPropagation();
  };

  return (
    showForm && (
      <div className={styles.addCardWrapper} onClick={toggleAddCard}>
        <img src={image} />
        <div className={styles.addCardForm} onClick={handleFormClick}>
          <h1>My name is</h1>
          <input
            type="text"
            placeholder="Ryan"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <h1>I can teach</h1>
          <input
            type="text"
            placeholder="ex: cooking"
            value={skill}
            onChange={e => setSkill(e.target.value)}
          />
          <h2>reach out to me at:</h2>
          <input
            type="text"
            placeholder="ryan.example.com"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </div>
      </div>
    )
  );
};

export default AddCard;
// https://reactjs.org/docs/hooks-faq.html#are-hooks-slow-because-of-creating-functions-in-render
