import React from "react";
import styles from "./index.module.scss";

const cssHSL = () =>
  "hsl(" +
  360 * Math.random() +
  "," +
  (20 + 30 * Math.random()) +
  "%," +
  (50 + 10 * Math.random()) +
  "%)";

const Card = ({ name, email, title }) => {
  const color = cssHSL();
  return (
    <div className={styles.card} style={{ background: color }}>
      I can teach you
      <h1>{title}</h1>
      <div>{name}</div>
      <div>{email}</div>
    </div>
  );
};

export default Card;
