import React from "react";
import styles from "./index.module.scss";

const cssHSL = () =>
  "hsl(" +
  360 * Math.random() +
  "," +
  (80 + 10 * Math.random()) +
  "%," +
  (60 + 20 * Math.random()) +
  "%)";

const Card = ({ name, email, title }) => {
  const color = cssHSL();
  return (
    <div
      className={styles.card}
      style={{ background: linearGradient(color, "#9198e5") }}
    >
      I can teach you
      <h1>{title}</h1>
      <div>{name}</div>
      <div>{email}</div>
    </div>
  );
};

export default Card;
