import React from "react";
import styles from "./index.module.scss";

const cssHSL = () =>
  "hsl(" +
  360 * Math.random() +
  "," +
  (80 + 10 * Math.random()) +
  "%," +
  (60 + 20 * Math.random()) +
  "%)";
const colorQ = () => {
  const rand = Math.floor(Math.random() * 10);

  return (
    "rgb(" +
    (215 - rand * 3) +
    "," +
    (185 - rand * 5) +
    "," +
    (185 - rand * 10) +
    " )"
  );
};

const Card = ({ name, email, title }) => {
  const color = colorQ();

  return (
    <div
      className={styles.card}
      style={{ background: `linear-gradient(${color}, #FFF)` }}
    >
      I can teach you
      <h1>{title}</h1>
      <div>{name}</div>
      <div>{email}</div>
    </div>
  );
};

export default Card;
