import React from "react";
import styles from "./index.module.scss";

const cssBright = () =>
  "hsl(" +
  360 * Math.random() +
  "," +
  (80 + 20 * Math.random()) +
  "%," +
  (35 + 20 * Math.random()) +
  "%)";

const cssLight = () =>
  "hsl(" +
  360 * Math.random() +
  "," +
  (20 + 30 * Math.random()) +
  "%," +
  (30 + 70 * Math.random()) +
  "%)";

const Card = ({ name, email, title }) => {
  const color = cssBright();
  const color2 = cssLight();

  return (
    <div
      className={styles.card}
      style={{ background: `linear-gradient(${color}, ${color2})` }}
    >
      I can teach you
      <h1>{title}</h1>
      <div>{name}</div>
      <div>{email}</div>
    </div>
  );
};

export default Card;
