import React from "react";
import styles from "./index.module.scss";

const randomHSL = () => {
  return "hsl(" + parseInt(256 * Math.random()) + "," + "70%," + "85%,1)";
};
const colorQ = () => {
  const rand = Math.floor(Math.random() * 10);

  return (
    "rgb(" +
    (215 - rand * 3) +
    "," +
    (185 - rand * 5) +
    "," +
    (185 - rand * 10) +
    " )"
  );
};

const Card = ({ name, email, title }) => {
  const color = randomHSL();

  return (
    <div
      className={styles.card}
      style={{ background: `linear-gradient(${color}, #FFF)` }}
    >
      I can teach you
      <h1>{title}</h1>
      <div>{name}</div>
      <div>{email}</div>
    </div>
  );
};

export default Card;
