import React from "react";
import styles from "./index.module.scss";

const cssHSL = () =>
  "hsl(" +
  180 * Math.random() +
  "," +
  (80 + 10 * Math.random()) +
  "%," +
  (60 + 20 * Math.random()) +
  "%)";

const cssHSL2 = () =>
  "hsl(" +
  180 +
  180 * Math.random() +
  "," +
  (80 + 10 * Math.random()) +
  "%," +
  (60 + 20 * Math.random()) +
  "%)";

const Card = ({ name, email, title }) => {
  const color = cssHSL();
  const color2 = cssHSL2();

  return (
    <div
      className={styles.card}
      style={{ background: `linear-gradient(${color}, ${color2})` }}
    >
      I can teach you
      <h1>{title}</h1>
      <div>{name}</div>
      <div>{email}</div>
    </div>
  );
};

export default Card;
